        # mark as seen by the current user
        try:
            journalist = session.get_user()
            if fn.endswith("reply.gpg"):
                reply = Reply.query.filter(Reply.filename == fn).one()
                mark_seen([reply], journalist)
            elif fn.endswith(("-doc.gz.gpg", "doc.zip.gpg")):
